    @Test
    public void getAllCurrenciesTest() {


        try (MockedStatic<DocumentUtil> utilities = Mockito.mockStatic(DocumentUtil.class)) {
            utilities.when(() -> DocumentUtil.getDocumentFromRestAsXml(Mockito.any()))
                    .thenReturn(createDocument());
            List<CurrencyDTO> currencyDTOList = currencyService.getAllCurrencies();
            Assert.assertEquals(currencyDTOList.size(), 212);
        }
    }

